mapper.select:
select  from pe_channel
where is_delete != 1 and  teach_id = #{teachId}
order by update_time desc
limit #{offset},#{pageSize}

mapper.insert:
insert into pe_channel(
teach_id, business_id, cover_url, `type`, create_time
)
values(
#{teachId}, #{businessId}, #{coverUrl}, #{type}, #{createTime}
)

mapper.update:
update pe_channel
set unread_count = #{num}
where id=#{id}
