mapper.insert:
insert into pr_class_student(
class_id, stu_id, join_time
)
values(
#{classId}, #{stuId}, #{joinTime}
)

mapper.select:
select count(id) from pr_class_student
where is_delete != 1 and is_quit = 1 and class_id = #{classId}
and to_days(quit_time) = to_days(now())

mapper.update:
update pr_class_student
set is_quit = #{isQuit},
quit_type = #{quitType},
is_rejoin = #{isRejoin},
quit_time = #{quitTime}
where id=#{id}

update pr_class_student
set qr_code = #{url}
where id=#{id}
