mapper.select:
select  from pe_stu_message
where is_delete !=1 and stu_id = #{stuId} and class_id = #{classId}
order by create_time desc
limit #{offset},#{pageSize}

select DISTINCT class_id, business_id, `type` from pe_stu_message
where is_delete !=1 and class_id = #{classId}
order by create_time desc
limit #{offset},#{pageSize}
